/**************************************************************************
*
*  System:    ToDo (Web)
*  Module:    Client App \ Shared \ Utilities
*  Date:      04-AUG-2023
*  Author:    Gary Moran (GM)
*  Function:  Control Validation
*  Notes:     
*
*                   : History of Amendments :
*  Date        Name        Brief description                
*  ----------- ----------  ---------------------------------------------
*  04-AUG-2023 GM          Created
************************************************************************/

import { AbstractControl, FormGroup } from "@angular/forms";
import { ElementFocus } from "./element-focus";

export class ControlValidation {
/**
 * Is Valid
 * @param errorString
 * @returns true
 */
  public static isInvalid(form: FormGroup, name: string): boolean {

    let returnValue = false;
    const formControl: AbstractControl | null = form.get(name);

    if (formControl == null)
      returnValue = false;
    else {
      if (ElementFocus.elementHasFocus(name))
        returnValue = false;
      else
        returnValue = formControl.invalid && (formControl.dirty || formControl.touched)
    }

    return returnValue;

  }

}
