import { Injectable } from '@angular/core';
import {
  AbstractControl,
  AsyncValidator,
  ValidationErrors,
} from '@angular/forms';
import { Observable, of } from 'rxjs';
import { catchError, map } from 'rxjs/operators';
import { Generic } from '../models/generic';
import { AuthenticationService } from '../services/authentication.service';
import { ViewModelDataService } from '../services/view-model-data.service';

@Injectable({ providedIn: 'root' })
export class UsernameValidator implements AsyncValidator {

  constructor(private viewModelDataservice: ViewModelDataService) { }

  validate(
    control: AbstractControl
  ): Observable<ValidationErrors | null> {
    return this.viewModelDataservice.actionViewModel<Generic>(new Generic(control.value), "account", "CheckUsername")
      .pipe(
        map((available: Generic) => (available.value.toLowerCase() === "true" ? null : { "usernameValidator-MSG": "This username is not available" } )),
      catchError(() => of(null))
    );
  }
}
