************************************************************************/

import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MessageService } from '../../shared/services/message.service';
import { ControlValidation } from '../../shared/utilities/control-validation';
import { ElementFocus } from '../../shared/utilities/element-focus';
import { Whitelist } from '../../shared/models/whitelist';
import { WhitelistService } from '../../shared/services/whitelist.service';
import { TextValidators } from '../../shared/validators/text.validator';
import { AuthenticationService } from '../../shared/services/authentication.service';
import { Login } from '../../shared/models/login';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html'
})
export class RegisterComponent implements OnInit {

  elementHasFocus = ElementFocus.elementHasFocus;
  isControlInvalid = ControlValidation.isInvalid;

  nameWhitelist: Whitelist = new Whitelist();
  descWhitelist: Whitelist = new Whitelist();
  usernameWhitelist: Whitelist = new Whitelist();
  passwordWhitelist: Whitelist = new Whitelist();

  isProcessing: boolean = false;
  registerForm!: FormGroup;

  constructor(private authenticationService: AuthenticationService, private router: Router, private formBuilder: FormBuilder, private whitelistService: WhitelistService, private messageService: MessageService,) { }

  async ngOnInit(): Promise<void> {
    this.nameWhitelist = await this.whitelistService.getWhitelist("NAME");
    this.descWhitelist = await this.whitelistService.getWhitelist("DESC");
    this.usernameWhitelist = await this.whitelistService.getWhitelist("USERNAME");
    this.passwordWhitelist = await this.whitelistService.getWhitelist("PASSWORD");
    this.initForm();
  }

  /**
   * Initialise Form
   */
  initForm() {
    this.registerForm = this.formBuilder.group({
      firstname: ['', { validators: [Validators.required, TextValidators.whitelistValidator(this.nameWhitelist)], updateOn: 'blur' }],
      lastname: ['', { validators: [Validators.required, TextValidators.whitelistValidator(this.nameWhitelist)], updateOn: 'blur' }],
      email: ['', { validators: [Validators.required, TextValidators.whitelistValidator(this.descWhitelist), TextValidators.minLengthValidator(5), TextValidators.emailValidator()], updateOn: 'blur' }],
      username: ['', { validators: [Validators.required, TextValidators.whitelistValidator(this.usernameWhitelist), TextValidators.minLengthValidator(4)], updateOn: 'blur' }],
      password: ['', { validators: [Validators.required, TextValidators.whitelistValidator(this.passwordWhitelist), TextValidators.minLengthValidator(8)], updateOn: 'blur' }],
      confirmPassword: ['', { validators: [Validators.required, TextValidators.whitelistValidator(this.passwordWhitelist), TextValidators.minLengthValidator(8)], updateOn: 'blur' }],
    });
  }

  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  matchPasswords(): { [key: string]: any } {
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    let errors: any = null;
    if (this.registerForm.get("password") !== this.registerForm.get("confirmPassword")) {
      errors = { 'noMatch-MSG': `The entered passwords do not match` };
    }
    return errors;
  }

  /**
   * Register
   */
  onRegister() {
  }

  /**
   * Cancel
   */
  onCancel() {
    this.isProcessing = true;
    this.registerForm.patchValue(new Login());
    this.isProcessing = false;
  }
}
<main class="container" role="main">

  <div class="card">

    <div class="card-header">
      <h5 class="card-title">Register</h5>
    </div>

    <div class="card-body">

      <form novalidate (ngSubmit)="onRegister()" [formGroup]="registerForm" *ngIf="registerForm">

        <div class="row g-2">

          <div class="mb-2 form-floating col-md-6">
            <input class="form-control" id="firstname" type="text" formControlName="firstname" placeholder="firstname" maxLength="100" />
            <element-validation-message [model]="registerForm.get('firstname')" *ngIf="!elementHasFocus('firstname') && isControlInvalid(registerForm.get('firstname'))"></element-validation-message>
            <label class="form-label" for="firstname">First Name</label>
          </div>

          <div class="mb-2 form-floating col-md-6">
            <input class="form-control" id="lastname" type="text" formControlName="lastname" placeholder="lastname" maxLength="100" />
            <element-validation-message [model]="registerForm.get('lastname')" *ngIf="!elementHasFocus('lastname') && isControlInvalid(registerForm.get('lastname'))"></element-validation-message>
            <label class="form-label" for="lastname">Last Name</label>
          </div>

        </div>

        <div class="row g-2">

          <div class="mb-2 form-floating col-md-6">
            <input class="form-control" id="email" type="text" formControlName="email" placeholder="email" maxLength="256" />
            <element-validation-message [model]="registerForm.get('email')" *ngIf="!elementHasFocus('email') && isControlInvalid(registerForm.get('email'))"></element-validation-message>
            <label class="form-label" for="email">Email</label>
          </div>

          <div class="mb-2 form-floating col-md-6">
            <input class="form-control" id="username" type="text" formControlName="username" placeholder="username" maxLength="40" />
            <element-validation-message [model]="registerForm.get('username')" *ngIf="!elementHasFocus('username') && isControlInvalid(registerForm.get('username'))"></element-validation-message>
            <label class="form-label" for="username">Username</label>
          </div>

        </div>

        <div class="row g-2">

          <div class="mb-2 form-floating col-md-6">
            <input class="form-control" id="password" type="password" formControlName="password" placeholder="password" maxLength="40" />
            <element-validation-message [model]="registerForm.get('password')" *ngIf="!elementHasFocus('password') && isControlInvalid(registerForm.get('password'))"></element-validation-message>
            <label class="form-label" for="password">Password</label>
          </div>

          <div class="mb-2 form-floating col-md-6">
            <input class="form-control" id="confirmPassword" type="password" formControlName="confirmPassword" placeholder="confirmPassword" maxLength="40" />
            <element-validation-message [model]="registerForm.get('confirmPassword')" *ngIf="!elementHasFocus('confirmPassword') && isControlInvalid(registerForm.get('confirmPassword'))"></element-validation-message>
            <label class="form-label" for="confirmPassword">Confirm Password</label>
          </div>

        </div>

        <div class="gap-2 d-flex justify-content-end">
          <button type="submit" class="btn btn-secondary" (click)="onRegister()" [disabled]="isProcessing || !this.registerForm.valid">Register</button>
          <button type="button" class="btn btn-secondary" (click)="onCancel()" [disabled]="isProcessing">Cancel</button>
        </div>

      </form>
    </div>
  </div>

</main>
