import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { ModalReturn, ModalType } from '../shared/components/modal-dialog/modal-dialog.component';
import { AuthenticationService } from '../shared/services/authentication.service';
import { ModalDialogService } from '../shared/services/modal-dialog.service';

@Component({
  selector: 'app-nav-menu',
  templateUrl: './nav-menu.component.html',
  styleUrls: ['./nav-menu.component.css']
})
export class NavMenuComponent {
  isExpanded = false;

  constructor(private authenticationService: AuthenticationService, private router: Router, private modalDialogService: ModalDialogService) { }

  collapse() {
    this.isExpanded = false;
  }

  toggle() {
    this.isExpanded = !this.isExpanded;
  }

  async logout() {
    if (this.authenticationService.isAuthenticated()) {
      if (await this.modalDialogService.callModalDialog(ModalType.YesNo, "Are you sure you wish to logout?") == ModalReturn.Yes) {
        this.authenticationService.logout();
        this.router.navigate(["login"]);
      }
    }
  }
}
<header>
  <nav class="navbar navbar-expand-sm navbar-toggleable-sm navbar-dark bg-dark border-bottom box-shadow mb-3">
    <div class="container">
      <a class="navbar-brand" [routerLink]="['/']">To-do's App</a>
      <button class="navbar-toggler"
              type="button"
              data-toggle="collapse"
              data-target=".navbar-collapse"
              aria-label="Toggle navigation"
              [attr.aria-expanded]="isExpanded"
              (click)="toggle()">
        <span class="navbar-toggler-icon"></span>
      </button>
      <div class="navbar-collapse collapse d-sm-inline-flex justify-content-end"
           [ngClass]="{ show: isExpanded }">
        <ul class="navbar-nav flex-grow">
          <li class="nav-item"
              [routerLinkActive]="['link-active']"
              [routerLinkActiveOptions]="{ exact: true }">
            <a class="nav-link" [routerLink]="['/']">Home</a>
          </li>
          <li class="nav-item">
            <a class="nav-link" (click)="logout()">Logout</a>
          </li>
          <!--<li class="nav-item" [routerLinkActive]="['link-active']">
            <a class="nav-link" [routerLink]="['/login']">Login</a>
          </li>-->
          <li class="nav-item" [routerLinkActive]="['link-active']">
            <a class="nav-link" [routerLink]="['/todo']">To-do list</a>
          </li>
        </ul>
      </div>
    </div>
  </nav>
  <app-messages [subject]='"nav-menu"' [disposeInterval]='3000' data-toggle="tooltip" title="{{'Press Alt + X to close using the keyboard.'}}" [persistMessageTypesParam]='["Danger"]'></app-messages>
</header>
