import { isNullOrUndefined } from 'is-what';
export class ElementFocus {
  /**
   * Set Focus on Element
   * @param elementId Element ID
   * @param index index
   */
  static setFocusOnElement(elementId, index) {
    // TODO: this should work but doesn't ???
    //elementId = isNullOrUndefined(index) ? elementId : `${elementId}-${index}`;
    //let element = document.getElementById(elementId);
    //this.setElementFocus(element);
    setTimeout(() => {
      elementId = isNullOrUndefined(index) ? elementId : `${elementId}-${index}`;
      const element = document.getElementById(elementId);
      if (element) element.focus();
    }, 200);
  }
  /**
   * Set Element Focus
   * @param element Element
   */
  static setElementFocus(element) {
    setTimeout(() => {
      if (document.body.contains(element)) element.focus();
    }, 200);
  }
  /**
   * Element Has Focus
   * @param elementId Element Id
   * @param index Index
   */
  static elementHasFocus(elementId, index) {
    elementId = isNullOrUndefined(index) ? elementId : `${elementId}-${index}`;
    const element = document.getElementById(elementId);
    return element == document.activeElement;
  }
}
