/**************************************************************************
*
*  System:    ToDo (Web)
*  Module:    Client App \ Todo
*  Date:      09 AUG 2023
*  Author:    Gary Moran (GM)
*  Function:  Login
*  Notes:     
*
*                   : History of Amendments :
*  Date        Name        Brief description                
*  ----------- ----------  ---------------------------------------------
*  09 AUG 2023 GM          Created
************************************************************************/

import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MessageService } from '../../shared/services/message.service';
import { ViewModelDataService } from '../../shared/services/view-model-data.service';
import { ControlValidation } from '../../shared/utilities/control-validation';
import { ElementFocus } from '../../shared/utilities/element-focus';
import { Whitelist } from '../../shared/models/whitelist';
import { WhitelistService } from '../../shared/services/whitelist.service';
import { TextValidators } from '../../shared/validators/text.validator';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{

  elementHasFocus = ElementFocus.elementHasFocus;
  isControlInvalid = ControlValidation.isInvalid;

  usernameWhitelist: Whitelist = new Whitelist();
  passwordWhitelist: Whitelist = new Whitelist();

  isProcessing: boolean = false;
  loginForm!: FormGroup;

  constructor(private viewModelDataService: ViewModelDataService, private whitelistService: WhitelistService, private formBuilder: FormBuilder, private messageService: MessageService,) { }

  async ngOnInit(): Promise<void> {
    this.usernameWhitelist = await this.whitelistService.getWhitelist("USERNAME");
    this.passwordWhitelist = await this.whitelistService.getWhitelist("PASSWORD");
    this.initForm();
  }

  /**
   * Initialise Form
   */
  initForm() {
    this.loginForm = this.formBuilder.group({
      username: ['', { validators: [Validators.required, TextValidators.whitelistValidator(this.usernameWhitelist), TextValidators.minLengthValidator(4)], updateOn: 'blur' }],
      password: ['', { validators: [Validators.required, TextValidators.whitelistValidator(this.passwordWhitelist), TextValidators.minLengthValidator(8)], updateOn: 'blur' }],
    });
  }

  onLogin() { }

  onCancel() { }
}
<main class="container" role="main">

  <!--<div class="row">
    <h1>Login</h1>
  </div>-->

  <div class="card">

    <div class="card-header">
      <h5 class="card-title">Login</h5>
    </div>

    <div class="card-body">

      <form novalidate (ngSubmit)="onLogin()" [formGroup]="loginForm" *ngIf="loginForm">

        <div class="row g-2">

          <div class="mb-2 form-floating col-md-6">
            <input class="form-control" id="username" type="text" formControlName="username" placeholder="username" minlength="4" maxLength="40" />
            <element-validation-message [model]="loginForm.get('username')" *ngIf="!elementHasFocus('username') && isControlInvalid(loginForm.get('username'))"></element-validation-message>
            <label class="form-label" for="name">Name</label>
          </div>

          <div class="mb-2 form-floating col-md-6">
            <input class="form-control" id="password" type="password" formControlName="password" placeholder="password" minlength="8" maxLength="40" />
            <element-validation-message [model]="loginForm.get('password')" *ngIf="!elementHasFocus('password') && isControlInvalid(loginForm.get('password'))"></element-validation-message>
            <label class="form-label" for="password">Password</label>
          </div>

        </div>

        <div class="gap-2 d-flex justify-content-end">
          <button type="submit" class="btn btn-secondary" (click)="onLogin()" [disabled]="isProcessing || !this.loginForm.valid">Login</button>
          <button type="button" class="btn btn-secondary" (click)="onCancel()" [disabled]="isProcessing">Cancel</button>
        </div>

      </form>
    </div>
  </div>

</main>
