export class TextValidators {
  /**
  * Minimum Length Validator
  * @param length
  */
  static minLengthValidator(length) {
    return control => TextValidators.minLengthValidatorFn(control, length);
  }
  /**
  * Minimum Length Validator Function
  * @param control Control
  * @param length
  */
  static minLengthValidatorFn(control, length) {
    let errors = null;
    // Validate input    
    if (control.value !== null) {
      if (control.value !== undefined) {
        if (control.value != "") {
          if (!this.validateMinLength(control.value.toString(), length)) {
            errors = {
              'minLengthValidator-MSG': `A minimum of ${length} characters is required`
            };
          }
        }
      }
    }
    return errors;
  }
  /**
   * Validate Minimum Length
   * @param value
   * @param length
   */
  static validateMinLength(value, length) {
    return value.length >= length;
  }
  /**
  * Whitelist Validator
  * @param whitelist
  */
  static whitelistValidator(whitelist) {
    return control => TextValidators.whitelistValidatorFn(control, whitelist);
  }
  /**
  * Whitelist Validator Function
  * @param control
  * @param whitelist
  */
  static whitelistValidatorFn(control, whitelist) {
    let errors = null;
    // Validate input    
    if (control.value !== null) {
      if (control.value !== undefined) {
        if (control.value != "") {
          if (!this.validateWhitelist(control.value.toString(), whitelist)) {
            errors = {
              "whitelistValidator-MSG": whitelist.error
            };
          }
        }
      }
    }
    return errors;
  }
  /**
   * Validate Whitelist
   * @param value
   * @param whitelist
   */
  static validateWhitelist(value, whitelist) {
    const regex = new RegExp(whitelist.pattern);
    if (value.match(regex)) return true;else return false;
  }
}
