/**************************************************************************
*
*  System:    ToDo (Web)
*  Module:    Client App \ Todo
*  Date:      25 JUL 2023
*  Author:    Gary Moran (GM)
*  Function:  Todo Component
*  Notes:     
*
*                   : History of Amendments :
*  Date        Name        Brief description                
*  ----------- ----------  ---------------------------------------------
*  25 JUL 2023 GM          Created
************************************************************************/

import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { catchError, Observable, of, take } from 'rxjs';
import { Message } from '../../shared/models/message';
import { MessageType } from '../../shared/models/message-type.enum';
import { Todo } from '../../shared/models/todo';
import { MessageService } from '../../shared/services/message.service';
import { ViewModelDataService } from '../../shared/services/view-model-data.service';
import { ModalDialogService } from '../../shared/services/modal-dialog.service';
import { ModalReturn, ModalType } from '../../shared/components/modal-dialog/modal-dialog.component';
import { ElementFocus } from '../../shared/utilities/element-focus';
import { ControlValidation } from '../../shared/utilities/control-validation';
import { AuthenticationService } from '../../shared/services/authentication.service';

enum State {
  New,
  Edit,
}

@Component({
  selector: 'app-todo',
  templateUrl: './todo.component.html',
})
export class TodoComponent implements OnInit {

  elementHasFocus = ElementFocus.elementHasFocus;
  isControlInvalid = ControlValidation.isInvalid;

  username: string | null = null;
  isProcessing: boolean = false;
  todos$: Observable<void | Todo[]> | undefined;
  todo: Todo = new Todo();
  todoForm!: FormGroup;
  state: State = State.New;
  public stateType: typeof State = State;

  get currentId() {
    const id: number = this.todoForm.get('id')?.value ?? -1;
    return id;
  }

  constructor(
    private viewModelDataService: ViewModelDataService, private formBuilder: FormBuilder, private messageService: MessageService, private modalDialogService: ModalDialogService,
    private authenticationService: AuthenticationService, 
  ) { }

  ngOnInit(): void {
    this.authenticationService.username$.pipe(take(1)).subscribe(username => {
      this.username = username;
      this.refreshList();
      this.initForm();
    });
  }

  /**
   * Initialise Form
   */
  initForm() {
    this.todoForm = this.formBuilder.group({
      id: null,
      name: ['', { validators: [Validators.required], updateOn: 'blur' }],
      description: ['', { validators: [Validators.required], updateOn: 'blur' }],
      isComplete: null,
      updated: null,
    });
  }

  /**
   * Refresh List
   */
  refreshList(): void {
    this.todos$ = this.viewModelDataService.getViewModelList<Todo>("todo")
      .pipe(catchError(error => of(this.messageService.sendErrorMessage({ "message": error }))));
  }

  /**
   * Update Form
   * @param todo
   */
  updateForm(todo: Todo): void {
    this.todoForm.patchValue(todo);
    this.todoForm.markAsPristine();
    this.todoForm.markAsUntouched();
  }

  /**
   * Refresh Page
   * @param todo
   * @param setNew
   */
  refresh(todo: Todo, setNew: boolean = false): void {
    this.refreshList();
    this.updateForm(todo);
    if (setNew)
      this.state = State.New;
    else
      this.state = State.Edit;
  }

  /**
   * On Select
   * @param todo
   */
  onSelect(todo: Todo): void {
    this.updateForm(todo);
    this.state = State.Edit;
  }

  /**
   * On New
   */
  onNew(): void {
    this.isProcessing = true;
    this.updateForm(new Todo());
    this.state = State.New;
    ElementFocus.setFocusOnElement("name");
    this.isProcessing = false;
  }

  /**
   * On Save
   */
  async onSave(): Promise<void> {

    this.isProcessing = true;

    if (await this.modalDialogService.callModalDialog(ModalType.Save) == ModalReturn.Save) {

      const todo: Todo = new Todo(this.todoForm.value)
      let response: Todo | undefined;

      const saveMessage: string = this.state === State.New ? "Record Created" : "Record Updated";

      if (this.state === State.New) {
        await this.viewModelDataService.addViewModel<Todo>(todo, "todo").toPromise()
          .then(r => { if (r) response = r })
          .catch(error => this.messageService.sendErrorMessage(error));
      }
      else if (this.state === State.Edit && todo.id !== undefined) {
        await this.viewModelDataService.updateViewModel<Todo>(todo, "todo", todo.id).toPromise()
          .then(r => { if (r) response = r })
          .catch(error => this.messageService.sendErrorMessage(error));
      }

      if (response) {
        this.refresh(response);
        this.messageService.sendMessage(new Message("nav-menu", saveMessage, MessageType.Dark));
      }

    }

    this.isProcessing = false;

  }

  /**
   * On Delete
   */
  async onDelete(): Promise<void> {

    this.isProcessing = true;

    if (await this.modalDialogService.callModalDialog(ModalType.Delete) == ModalReturn.Delete) {

      let response: boolean = false;

      await this.viewModelDataService.deleteViewModel("todo", this.todoForm.value.id).toPromise()
        .then(r => { if (r) response = r })
        .catch(error => this.messageService.sendErrorMessage(error));
      if (response) {
        this.refresh(new Todo(), true);
        this.messageService.sendMessage(new Message("nav-menu", "Record Deleted", MessageType.Dark));
      }

    }

    this.isProcessing = false;

  }

}
<main class="container" role="main">

  <div class="row">
    <h1 id="tableLabel">To-do List</h1>
  </div>

  <div *ngIf="!todos$" class="row">
    <p><em>Loading...</em></p>
  </div>

  <div class="row" *ngIf="todos$ | async as todos">
    <div class="table-responsive">
      <table class='table table-hover table-bordered' aria-labelledby="tableLabel" *ngIf="todos">
        <thead class="table-dark">
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Description</th>
            <th>Completed</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let todo of todos;let i = index" [ngClass]="{'table-active': state === stateType.Edit && todo.id == currentId}" (click)="onSelect(todo)">
            <td>{{ todo.id }}</td>
            <td>{{ todo.name }}</td>
            <td>{{ todo.description }}</td>
            <td>
              <div class="form-check form-switch">
                <input class="form-check-input" type="checkbox" [checked]="todo.isComplete" disabled />
              </div>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>

  <div class="card">

    <div class="card-header">
      <h5 class="card-title">To-do Item</h5>
    </div>

    <div class="card-body">

      <form novalidate (ngSubmit)="onSave()" [formGroup]="todoForm">

        <div class="row g-2">

          <div class="form-floating col-md-4">
            <input class="form-control" id="id" type="text" formControlName="id" readonly placeholder="id" />
            <label class="form-label" for="id">ID</label>
          </div>

          <div class="mb-2 form-floating col-md-8">
            <input class="form-control" id="name" type="text" formControlName="name" placeholder="name" maxLength="20" />
            <element-validation-message [model]="todoForm.get('name')" *ngIf="!elementHasFocus('name') && isControlInvalid(todoForm.get('name'))"></element-validation-message>
            <label class="form-label" for="name">Name</label>
          </div>

        </div>

        <div class="mb-2 form-floating">
          <input class="form-control" id="description" type="text" formControlName="description" placeholder="description" maxLength="100" />
          <element-validation-message [model]="todoForm.get('description')" *ngIf="!elementHasFocus('description') && isControlInvalid(todoForm.get('description'))"></element-validation-message>
          <label class="form-label" for="description">Description</label>
        </div>

        <div class="form-check form-switch mb-2">
          <input class="form-check-input" id="is-complete" type="checkbox" formControlName="isComplete" />
          <label class="form-check-label" for="is-complete">Completed</label>
        </div>

        <div class="gap-2 d-flex justify-content-end">
          <button type="button" class="btn btn-secondary" (click)="onNew()" [disabled]="isProcessing">New</button>
          <button type="submit" class="btn btn-secondary" [disabled]="isProcessing || !this.todoForm.valid">Save</button>
          <button type="button" class="btn btn-secondary" (click)="onDelete()" [disabled]="isProcessing || state !== stateType.Edit">Delete</button>
        </div>

      </form>
    </div>
  </div>

</main>
