export class TextValidators {
  /**
  * whitelist Validator
  * @param doValidate
  */
  static whitelistValidator(whitelist) {
    return control => TextValidators.whitelistValidatorFn(control, whitelist);
  }
  /**
  * Whitelist Validator Function
  * @param control Control
  */
  static whitelistValidatorFn(control, whitelist) {
    let errors = null;
    // Validate input    
    if (control.value !== null) {
      if (control.value !== undefined) {
        if (control.value != "") {
          if (!this.validateWhitelist(control.value.toString(), whitelist)) {
            errors = {
              'whitelistValidator-MSG': whitelist.error
            };
          }
        }
      }
    }
    return errors;
  }
  /**
   * validateWhitelist Validate whitelist
   * @param whitelist whitelist
   */
  static validateWhitelist(value, whitelist) {
    const regex = new RegExp(whitelist.pattern);
    if (value.match(regex)) return true;else return false;
  }
}
